import React, { useState } from 'react';
import axios from 'axios';
import './FileUploadForm.css'; // Importing the CSS file

const FileUploadForm = () => {
    const [files, setFiles] = useState(null); // State to store selected files
    const [urls, setUrls] = useState(''); // State to store URLs
    const [loading, setLoading] = useState(false); // Loading state
    const [response, setResponse] = useState(''); // Store response from server
    const [error, setError] = useState(null); // Error handling

    // Handle file input change
    const handleFileChange = (e) => {
        setFiles(e.target.files);
    };

    // Handle URL input change
    const handleUrlChange = (e) => {
        setUrls(e.target.value);
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setResponse('');
        setError(null);

        const formData = new FormData();

        // Append selected files to formData
        if (files) {
            for (let i = 0; i < files.length; i++) {
                formData.append('files', files[i]);
            }
        }

        // Append URLs to formData
        formData.append('urls', urls);

        try {
            // Send form data to the backend
            const res = await axios.post('http://localhost:5000/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            setResponse(res.data); // Set response from the server
        } catch (err) {
            setError('Error submitting data, please try again.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="upload-form-container">
            <h1>Upload Files and URLs</h1>
            <form onSubmit={handleSubmit}>
                <div className="file-input-container">
                    <label htmlFor="files">Select Files:</label>
                    <input type="file" id="files" onChange={handleFileChange} multiple accept=".txt,.docx" />
                </div>

                <div className="url-input-container">
                    <label htmlFor="urls">Enter URLs (separated by commas):</label>
                    <input
                        type="text"
                        id="urls"
                        value={urls}
                        onChange={handleUrlChange}
                        placeholder="Enter URLs here"
                    />
                </div>

                <button type="submit" className="upload-btn" disabled={loading || (!files && !urls)}>
                    {loading ? 'Submitting...' : 'Submit'}
                </button>
            </form>

            <div className="response-container">
                {error && <p className="error">{error}</p>}
                {response && <p className="response">{response}</p>}
            </div>
        </div>
    );
};

export default FileUploadForm;
