import React, { useState } from 'react';
import './FloatingButton.css'; // Styling for the floating button

const FloatingButton = () => {
    const [isOpen, setIsOpen] = useState(false);

    // Toggle the chatbot window
    const toggleChatbot = () => {
        setIsOpen(!isOpen);
    };

    return (
        <div>
            {/* The floating button */}
            <button className="floating-btn" onClick={toggleChatbot}>
                ðŸ’¬ Chat
            </button>

            {/* The chatbot window */}
            {isOpen && (
                <div className="chatbot-window">
                    <div className="chatbot-header">
                        <h3>Chatbot</h3>
                        <button className="close-btn" onClick={toggleChatbot}>âœ–</button>
                    </div>
                    <div className="chatbot-body">
                        {/* You can integrate your chatbot UI here */}
                        <p>Hello! How can I assist you today?</p>
                    </div>
                </div>
            )}
        </div>
    );
};

export default FloatingButton;
