import React, { useState } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify'; // Import toast

const FilesInputForm = () => {
    const [files, setFiles] = useState(null);

    const handleFileChange = (e) => {
        setFiles(e.target.files);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        for (let i = 0; i < files.length; i++) {
            formData.append('files', files[i]);
        }
        try {
            const res = await axios.post('http://localhost:5000/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            toast.success('Files uploaded successfully!'); // Show success toast
        } catch (err) {
            toast.error('Failed to upload files!'); // Show error toast
        }
    };

    return (
        <div>
            <h2>Submit Files (PDF, CSV, Text)</h2>
            <form onSubmit={handleSubmit}>
                <input type="file" onChange={handleFileChange} multiple accept=".pdf,.csv,.txt" />
                <br />
                <button type="submit">Upload</button>
            </form>
        </div>
    );
};

export default FilesInputForm;
