import React, { useState } from 'react';
import './FloatingButton.css';
import axios from 'axios'; // Axios for sending the prompt to the server

const FloatingButton = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [prompt, setPrompt] = useState(''); // State to store the user input (prompt)
    const [messages, setMessages] = useState([]); // State to store the chat messages

    // Toggle the chatbot window
    const toggleChatbot = () => {
        setIsOpen(!isOpen);
    };

    // Handle input submission
    const handleSubmit = async (e) => {
        e.preventDefault();

        if (prompt.trim()) {
            // Add user's message to chat
            const userMessage = { sender: 'user', text: prompt };
            setMessages((prevMessages) => [...prevMessages, userMessage]);

            try {
                // Send the prompt to the backend (replace '/chatbot' with your actual backend endpoint)
                const response = await axios.post('http://localhost:5000/chatbot', { prompt });

                // Add the chatbot's response to chat
                const botMessage = { sender: 'bot', text: response.data.reply };
                setMessages((prevMessages) => [...prevMessages, botMessage]);
            } catch (error) {
                console.error('Error sending prompt:', error);
                const botErrorMessage = { sender: 'bot', text: 'Error: Unable to get a response.' };
                setMessages((prevMessages) => [...prevMessages, botErrorMessage]);
            }

            // Clear the prompt input field
            setPrompt('');
        }
    };

    return (
        <div>
            {/* The floating button */}
            <button className="floating-btn" onClick={toggleChatbot}>
                ðŸ’¬ Chat
            </button>

            {/* The chatbot window */}
            {isOpen && (
                <div className="chatbot-window">
                    <div className="chatbot-header">
                        <h3>Chatbot</h3>
                        <button className="close-btn" onClick={toggleChatbot}>âœ–</button>
                    </div>
                    <div className="chatbot-body">
                        {/* Chat history */}
                        <div className="chat-messages">
                            {messages.map((msg, index) => (
                                <div key={index} className={msg.sender === 'user' ? 'user-message' : 'bot-message'}>
                                    <p>{msg.text}</p>
                                </div>
                            ))}
                        </div>
                        {/* Input form for the user prompt */}
                        <form onSubmit={handleSubmit} className="chat-input-form">
                            <input
                                type="text"
                                value={prompt}
                                onChange={(e) => setPrompt(e.target.value)}
                                placeholder="Type your message..."
                                required
                            />
                            <button type="submit">Send</button>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
};

export default FloatingButton;
