import React, { useState } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify'; // Import toast

const WebsiteInputForm = () => {
    const [url, setUrl] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const res = await axios.post('http://localhost:5000/upload', { url });
            toast.success('URL submitted successfully!'); // Show success toast
        } catch (err) {
            toast.error('Failed to submit URL!'); // Show error toast
        }
    };

    return (
        <div>
            <h2>Submit Website URL</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="url"
                    value={url}
                    onChange={(e) => setUrl(e.target.value)}
                    placeholder="Enter website URL"
                />
                <br />
                <button type="submit">Submit</button>
            </form>
        </div>
    );
};

export default WebsiteInputForm;
