import React, { useState } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify'; // Import toast for notifications

const QAInputForm = () => {
    const [question, setQuestion] = useState('');
    const [answer, setAnswer] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Construct the data to send to the backend
        const qaData = {
            question: question,
            answer: answer
        };

        try {
            // Make a POST request to the backend
            const res = await axios.post('http://localhost:5000/save-qa', qaData);
            toast.success('Q&A submitted successfully!'); // Show success toast
            setQuestion(''); // Clear the inputs
            setAnswer('');
        } catch (err) {
            toast.error('Failed to submit Q&A!'); // Show error toast
        }
    };

    return (
        <div>
            <h2>Submit Q&A</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="question">Question:</label>
                    <input
                        type="text"
                        id="question"
                        value={question}
                        onChange={(e) => setQuestion(e.target.value)}
                        placeholder="Enter your question"
                        required
                    />
                </div>
                <div>
                    <label htmlFor="answer">Answer:</label>
                    <textarea
                        id="answer"
                        value={answer}
                        onChange={(e) => setAnswer(e.target.value)}
                        placeholder="Enter the answer"
                        rows="5"
                        required
                    ></textarea>
                </div>
                <br />
                <button type="submit">Submit</button>
            </form>
        </div>
    );
};

export default QAInputForm;
